import React, { SFC } from 'react';
import { Spin } from 'antd';
import { SpinProps } from 'antd/es/spin';
import styled from 'styled-components';

const Wrap = styled.div`
  text-align: center;
`;

const StyledSpin = styled(Spin)<SpinProps>`
  padding: 20px 0;
`;

type Props = {
  /**
   * 提示文字
   * @default 数据加载中
   */
  tip?: string;
};

const Loading = (props: Props) => {
  const { tip } = props;

  return (
    <Wrap>
      <StyledSpin tip={tip} />
    </Wrap>
  );
};

Loading.defaultProps = { tip: '数据加载中' };
export default Loading;
try {
    // @ts-ignore
    Loading.displayName = "Loading";
    // @ts-ignore
    Loading.__docgenInfo = { "description": "", "displayName": "Loading", "props": { "tip": { "defaultValue": { value: "\u6570\u636E\u52A0\u8F7D\u4E2D" }, "description": "\u63D0\u793A\u6587\u5B57", "name": "tip", "required": false, "type": { "name": "string" } } } };
    // @ts-ignore
    if (typeof STORYBOOK_REACT_CLASSES !== "undefined")
        // @ts-ignore
        STORYBOOK_REACT_CLASSES["src/components/Loading.tsx#Loading"] = { docgenInfo: Loading.__docgenInfo, name: "Loading", path: "src/components/Loading.tsx#Loading" };
}
catch (__react_docgen_typescript_loader_error) { }
